start.filterFormula:
AND(
  {!$Setup.DataAdmin__c.TurnOffFlow__c} = false,
  TEXT({!$Record.Status}) != "Closed",
  TEXT({!$Record.Status}) != "Canceled",
  OR(
    AND(
      ISCHANGED({!$Record.BD_Account_Manager__c}),
      {!$Record.BD_Account_Manager__r.Email} != {!$Record__Prior.BD_Account_Manager__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.BD_Account_Manager_2__c}),
      {!$Record.BD_Account_Manager_2__r.Email} != {!$Record__Prior.BD_Account_Manager_2__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.BD_Account_Manager_3__c}),
      {!$Record.BD_Account_Manager_3__r.Email} != {!$Record__Prior.BD_Account_Manager_3__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.Project_Director__c}),
      {!$Record.Project_Director__r.Email} != {!$Record__Prior.Project_Director__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.Senior_Project_Manager__c}),
      {!$Record.Senior_Project_Manager__r.Email} != {!$Record__Prior.Senior_Project_Manager__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.Project_Manager__c}),
      {!$Record.Project_Manager__r.Email} != {!$Record__Prior.Project_Manager__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.RM_CM_OM__c}),
      {!$Record.RM_CM_OM__r.Email} != {!$Record__Prior.RM_CM_OM__r.Email}
    ),
    AND(
      ISCHANGED({!$Record.AP_Approver__c}),
      {!$Record.AP_Approver__r.Email} != {!$Record__Prior.AP_Approver__r.Email}
      )
  )
)
